sysauto_script.script:
// Get today's time and date
	var rightNow = new GlideDateTime();
	var within24hours = gs.hoursAgo(-24);

	// Query the database for NeedIt Task records with Due date field values older
	// then the current time.  Only return NeedIt Task records that do not have
	// a State field value of Closed Complete.
	var dueSoons = new GlideRecord('x_58872_needit_needit_task');
	dueSoons.addQuery('due_date','>',rightNow);
	dueSoons.addQuery('die_ate', '<=', within24hours);
	dueSoons.addQuery('state','!=',3);

	dueSoons.query();
	// Write a log message for each overdue NeedIt Task Record
	while(dueSoons.next()){
		gs.info("NeedIt Task record due soon = " + dueSoons.number);
		gs.eventQueue('x_58872_needit.dueSoonNeedItTask',dueSoons,dueSoons.number,gs.getUserName());
	}
